  `course_time` varchar(25) NOT NULL COMMENT '授课时间',
  `course_teacher` varchar(20) NOT NULL COMMENT '主讲教师',
  PRIMARY KEY (`course_id`)
) ENGINE=InnoDB AUTO_INCREMENT=36 DEFAULT CHARSET=utf8 COMMENT='课程表';
