  `message_title` varchar(50) NOT NULL COMMENT '消息标题',
  `message_content` varchar(10000) NOT NULL COMMENT '消息内容',
  `message_type` varchar(1) NOT NULL COMMENT '消息类型1通知2会议记录3讨论',
  `publish_time` varchar(25) NOT NULL COMMENT '发布时间',
  PRIMARY KEY (`pm_id`)
) ENGINE=InnoDB AUTO_INCREMENT=19 DEFAULT CHARSET=utf8 COMMENT='项目消息';
CREATE TABLE `upload_file` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
